import { Component, OnInit } from '@angular/core';
import { PassengerService } from '../api/services/passenger.service';
import { FormBuilder,Validators } from '@angular/forms'
import { AuthService } from '../auth/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register-passenger',
  templateUrl: './register-passenger.component.html',
  styleUrls: ['./register-passenger.component.css']
})
export class RegisterPassengerComponent implements OnInit {

  constructor(private passengerService: PassengerService, private fb: FormBuilder
  ,private authService:AuthService,private router:Router) {

  }
  form = this.fb.group({
    email: ['', Validators.compose([Validators.required, Validators.email, Validators.min(3), Validators.max(254)])],
    firstName: ['', Validators.compose([Validators.required, Validators.min(3), Validators.max(254)])],
    lastName: ['', Validators.compose([Validators.required, Validators.min(3), Validators.max(254)])],
    isFemale: [true, Validators.required]
  })
  ngOnInit(): void { }

  checkPassenger(): void {
    const params = { email: this.form.get('email')?.value! }
    this.passengerService.findPassenger$Json(params).subscribe(this.login, e => {
      if (e.status != 404) {
        console.error(e)
      }
    });
  }
        

  register() {
    if (this.form.invalid) {
      return;
    }

    //console.log("Form values :",this.form.value)
    this.passengerService.registerPassenger({ body: this.form.value })
      .subscribe(this.login, console.error);
  }

  login = () => {
    this.authService.loginUser({ email: this.form.get('email')?.value! })
    this.router.navigate(['/search-flight'])

  }

  get formEmail() {
    return this.form.controls.email;
  }
  get formFirstName() {
    return this.form.controls.firstName;
  }
  get formLastName() {
    return this.form.controls.lastName;
  }
  get formGender() {
    return this.form.controls.isFemale;
  }



}
<form [formGroup] ="form">
  <div class="container d-grid gap-3" style="max-width: 700px;">

    <div class="form-floating">
      <input (blur)="checkPassenger()" placeholder="Email" formControlName="email" class="form-control" type="text" />
      <label for="email">Email</label>
      <div class="text-danger" *ngIf="formEmail.invalid && (formEmail.dirty || formEmail.touched)">
        <div *ngIf="formEmail.errors"><i class="fa fa-exclamation-circle"></i> Email is invalid</div>
      </div>

    </div>
    <div class="d-grid gap-3">

      <div class="form-floating">

        <input class="form-control" type="text" formControlName="firstName" id="firstName" placeholder="First Name" />
        <label class="form-label" for="firstName">First Name</label>
        <div class="text-danger" *ngIf="formFirstName.invalid && (formFirstName.dirty || formFirstName.touched)">
          <div *ngIf="formEmail.errors"><i class="fa fa-exclamation-circle"></i> FirstName is invalid</div>
        </div>

      </div>

      <div class="form-floating">

        <input placeholder="Last Name" class="form-control" formControlName="lastName" id="lastName" type="text" />
        <label class="form-label" for="lastName">Last Name</label>
        <div class="text-danger" *ngIf="formEmail.invalid && (formEmail.dirty || formEmail.touched)">
          <div *ngIf="formEmail.errors"><i class="fa fa-exclamation-circle"></i> Email is invalid</div>
        </div>

      </div>


      <div>

        <input id="isFemale" class="form-check-input" formControlName="isFemale" [value]=true type="radio" />
        <label class="ms-2 form-check-label" for="isFemale">Female</label>
      </div>
      <div>
        <input id="isFemale" class="form-check-input" formControlName="isFemale" [value]=false type="radio" />
        <label class="ms-2 form-check-label" for="isFemale">Male</label>
      </div>
    </div>
    <div class="d-flex justify-content-center center">
      <button (click)="register()" type="submit" class="btn btn-primary">Login <i class="fa fa-sign-in" aria-hidden="true"></i></button>
    </div>
  </div>

</form>
