import { Component, OnInit } from '@angular/core';
import { ActivatedRoute,Router } from '@angular/router';
import { FlightsService } from '../api/services/flights.service';
import { BookDto, FlightsRm } from '../api/models'
import { AuthService } from '../auth/auth.service';
import { FormBuilder,Validators } from '@angular/forms';



@Component({
  selector: 'app-book-flight',
  templateUrl: './book-flight.component.html',
  styleUrls: ['./book-flight.component.css']
})
export class BookFlightComponent implements OnInit {
  flightId = "not loaded";
  flight: FlightsRm = {}


  constructor(private route: ActivatedRoute, private flightservice: FlightsService, private router: Router
    , private authService: AuthService, private fb: FormBuilder) {

  }

  form = this.fb.group({
    number: [1, Validators.required]
  })


  ngOnInit(): void {
    if (!this.authService.currentUser) {
      this.router.navigate(['/register-passenger']);
    }
    this.route.paramMap.subscribe(p => this.findFlight(p.get("flightId")));

  }

  private findFlight = (flightId: string | null) => {
    this.flightId = flightId ?? "not passed";

    this.flightservice.findFlights$Json({ id: this.flightId }).subscribe(flight => this.flight = flight, this.HandleError);
  };

  private HandleError = (err: any) => {
    if (err.status == 404 || err.status==400) {
      alert("The flight is not found!");
      this.router.navigate(['/search-flight'])
    }
    console.log("error status code " + err.status);
    console.log("error status Message " + err.statusText);
    console.log(err)
  }
  book() {
    if (this.form.invalid) {
      return;
    }
    console.log(`booking ${this.form.get('number')?.value} passengers for the flight ${this.flightId}`)


    const booking: BookDto = {
      flightId: this.flight.id,
      passengerEmail: this.authService.currentUser?.email,
      numberOfSeats: this.form.get('number')?.value!
    }
    this.flightservice.bookFlights({ body: booking }).subscribe(_ => this.router.navigate(["/my-booking"]), this.HandleError)
    
  }


}
<h2>Book Flight</h2>

<div class="card text-dark mt-5 text-center" style="background-color:cornsilk;">
  <div class="card-body">
    <strong><h6 class="card-title">{{ flight.airline }}</h6></strong>
    <div class="card-title" style="color:brown">{{ flight.remainingNumberOfSeats }} Remaining Seats</div>
  </div>
  <div class="d-grid text-center g-2">
    <div class="row">
      <div class="col">
        <div class="col m-3">
          <i class="text-primary fas fa-plane-departure" style="font-size:20px"></i>
        </div>
        <div class="col">
          <i class="fa-solid fa-location-dot">
            {{ flight.departure?.place }}
          </i>
        </div>
        <div class="col m-2">
          <i class="fa-regular"></i>
          {{ flight.departure?.time | date }}
        </div>
        <div class="col m-2">
          <i class="fa-regular fa-clock"></i>
          {{ flight.departure?.time | date :'shortTime' }}
        </div>

      </div>
      <div class="col">
        <div style="display:flex;">
          <i class="fa-solid fa-circle" style="margin-top:50px;font-size:9px;"></i>
          <hr style="display:block; width:100%;margin-top:55px;" />
          <i class="fas fa-plane" style="margin-top:48px;"></i>
        </div>
        <i class="fas fa-usd">{{ flight.price }}</i>
        <br />

      </div>
      <div class="col">
        <div class="col m-3">
          <i class="text-primary fas fa-plane-arrival" style="font-size:20px"></i>
        </div>
        <div class="col">
          <i class="fa-solid fa-location-dot">
            {{ flight.arrival?.place }}
          </i>
        </div>
        <div class="col m-2">
          <i class="fa-regular"></i>
          {{ flight.arrival?.time | date }}
        </div>
        <div class="col m-2">
          <i class="fa-regular fa-clock"></i>
          {{ flight.arrival?.time | date :'shortTime' }}
        </div>
      </div>
    </div>
    <form [formGroup]="form" (ngSubmit)="book()">
      <div class="col">
        <div class="input-group">
          <div class="input-group-text">
            Seats For
          </div>
          <input formControlName='number' class="form-control" type="number" placeholder="enter number"/>
          <div class="input-group-text">Passenger(s)</div>
          <button type="submit" class="btn btn-primary"><i class="fas fa-ticket"></i> book  </button>
        </div>

      </div>
    </form>

  </div>

</div>
<div class="alert alert-danger">


</div>
