import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { FlightsService } from '../api/services/flights.service';
import { FlightsRm } from '../api/models'



@Component({
  selector: 'app-book-flight',
  templateUrl: './book-flight.component.html',
  styleUrls: ['./book-flight.component.css']
})
export class BookFlightComponent implements OnInit {
  flightId = "not loaded";
  flight: FlightsRm = {}


  constructor(private route: ActivatedRoute,private flightservice:FlightsService) {

  }


  ngOnInit(): void {
    this.route.paramMap.subscribe(p => this.findFlight(p.get("flightId")));

  }

  private findFlight = (flightId: string | null) => {
    this.flightId = flightId ?? "not passed";

    this.flightservice.findFlights$Json({ id: this.flightId }).subscribe(flight => this.flight = flight);
  };

}
<h2>Book Flight</h2>
<div class="d-grid text-center g-2">
  <div class="row">
    <div class="col">
      <div class="col m-3">
        <i class="text-primary fas fa-plane-departure" style="font-size:20px"></i>
      </div>
      <div class="col">
        <i class="fa-solid fa-location-dot">
          {{ flight.departure?.place }}
        </i>
      </div>
      <div class="col m-2">
        <i class="fa-regular"></i>
        {{ flight.departure?.time | date }}
      </div>
      <div class="col m-2">
        <i class="fa-regular fa-clock"></i>
        {{ flight.departure?.time | date :'shortTime' }}
      </div>

    </div>
    <div class="col">
      <div style="display:flex;">
        <i class="fa-solid fa-circle" style="margin-top:50px;font-size:9px;"></i>
        <hr style="display:block; width:100%;margin-top:55px;" />
        <i class="fas fa-plane" style="margin-top:48px;"></i>
      </div>
      <i class="fas fa-usd">{{ flight.price }}</i>
      <br />
      
    </div>
    <div class="col">
      <div class="col m-3">
        <i class="text-primary fas fa-plane-arrival" style="font-size:20px"></i>
      </div>
      <div class="col">
        <i class="fa-solid fa-location-dot">
          {{ flight.arrival?.place }}
        </i>
      </div>
      <div class="col m-2">
        <i class="fa-regular"></i>
        {{ flight.arrival?.time | date }}
      </div>
      <div class="col m-2">
        <i class="fa-regular fa-clock"></i>
        {{ flight.arrival?.time | date :'shortTime' }}
      </div>
    </div>
  </div>
</div>
